&gt;&amp;lt;/template&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;lt;script lang=&amp;quot;ts&amp;quot; setup name=&amp;quot;Person&amp;quot;&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  import &amp;#123;ref,watch,watchEffect&amp;#125; from &amp;#x27;vue&amp;#x27;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  // 数据&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  let temp = ref(0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  let height = ref(0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  // 方法&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  function changePrice()&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    temp.value += 10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  function changeSum()&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    height.value += 1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  // 用watch实现，需要明确的指出要监视：temp、height&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  watch([temp,height],(value)=&amp;gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    // 从value中获取最新的temp值、height值&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    const [newTemp,newHeight] = value&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    // 室温达到50℃，或水位达到20cm，立刻联系服务器&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if(newTemp &amp;gt;= 50 || newHeight &amp;gt;= 20)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      console.log(&amp;#x27;联系服务器&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&